import React from "react";
const taskList = ({ tasks, deleteTaks }) => {
  return (
    <h1>
      {task.map((task) => {
        <li key={task.id}>
          {task.text}
          <button
            onClick={() => {
              deleteTaks(task.id);
            }}
          >
            Delete
          </button>
        </li>;
      })}
    </h1>
  );
};
export default taskList;
