import React, { useState } from "react";
import TaskForm from "./taskForm";
import TaskList from "./taskListaskList";

const App = () => {
  const [tasks, setTask] = useState([]);
  const addTask = (taskText) => {
    setTask([...tasks, { id: Date.now(), text: taskText }]);
  };
  const deleteTaks = (taskId) => {
    setTask(tasks.filter((task) => task.id !== taskId));
  };
  return (
    <div>
      <h1>sdcs</h1>
      <TaskForm addTask={addTask} />
      <taskList task={tasks} deleteTaks={deleteTaks} />
    </div>
  );
};

export default App;
